package entity;import java.util.ArrayList;import java.util.Iterator;import java.util.Set;public class User {    private int userId;    private Set<Item> ratedItems;    private Set<Score> itemScores;    public User(int userId) {        this.userId = userId;    }    public void rate (Item item, double rate){        this.ratedItems.add(item);        this.itemScores.add(new Score(item, rate));    }    public int getUserId() {        return userId;    }    public Set<Item> getRatedItems() {        return ratedItems;    }    public double getItemScores(Item item) {        Iterator<Score> iter = itemScores.iterator();        while (iter.hasNext()){            Score itemScore = iter.next();            if (itemScore.getItem().getItemId() == item.getItemId()){                return itemScore.getScore();            }        }        return -0.01;    }    @Override    public String toString() {        return "user [userId=" + userId + "]";    }}